import React from 'react';
import {Link} from "react-router-dom";
import classNames from "classnames";
import {URL_LOGOUT, URL_PROFIL} from "../../routes";
import {isEmptyObject} from "../../Util/core";
import AppBar from "@material-ui/core/es/AppBar/AppBar";
import Toolbar from "@material-ui/core/es/Toolbar/Toolbar";
import IconButton from "@material-ui/core/es/IconButton/IconButton";
import MenuIcon from '@material-ui/icons/es/Menu';
import Typography from "@material-ui/core/es/Typography/Typography";
import AccountCircle from "@material-ui/icons/es/AccountCircle";
import Menu from "@material-ui/core/es/Menu/Menu";
import MenuItem from "@material-ui/core/es/MenuItem/MenuItem";

class UIAppBar extends React.Component
{
    render() {
        const {classes, t, user} = this.props;
        const open = (this.props.open && !isEmptyObject(user));

        return (
            <AppBar
                className={classNames(classes.appBar, {[classes.appBarShift]: open})}
                position={"fixed"}
                component="div"
            >
                <Toolbar>
                    {this.renderUserMenuAction()}
                    <Typography variant="title" color="inherit" noWrap className={classes.flex}>
                        {t('menu.title')}
                    </Typography>
                    {this.renderLanguageMenu()}
                    {this.renderUserMenu()}
                </Toolbar>
            </AppBar>
        );
    }

    renderUserMenuAction() {
        const {user, classes} = this.props;
        const open = (this.props.open && !isEmptyObject(user));

        if (isEmptyObject(user)) {
            return;
        }

        return (
            <IconButton
                color="inherit"
                aria-label="open drawer"
                onClick={this.props.handleDrawerOpen}
                className={classNames(classes.menuButton, open && classes.hide)}
            >
                <MenuIcon />
            </IconButton>
        );
    }

    renderLanguageMenu() {
        return (
            <div>
                <IconButton
                    color="inherit"
                    onClick={this.props.handleChangeLanguageFr}
                >
                    <img
                        src={"data:image/png;base64,"} />
                </IconButton>
                <IconButton
                    color="inherit"
                    onClick={this.props.handleChangeLanguageEn}
                >
                    <img
                        src={"data:image/png;base64,"} />
                </IconButton>
            </div>
        );
    }

    renderUserMenu() {
        const {user} = this.props;
        if (isEmptyObject(user)) {
            return;
        }

        const {classes, t, i18n, anchorEl} = this.props;
        const userOpen = Boolean(anchorEl);

        return (
            <div>
                <IconButton
                    key={"icon"}
                    aria-owns={userOpen ? 'menu-appbar' : null}
                    aria-haspopup="true"
                    onClick={this.props.handleMenu}
                    color="inherit"
                >
                    {user.imgProfil != null && user.imgProfil.publicPath && user.imgProfil.publicPath.length
                        ? <img src={'http://site.local:8080/' + user.imgProfil.publicPath} className={classes.imgProfil} />
                        : <AccountCircle />
                    }
                </IconButton>
                <Menu
                    key={"menu"}
                    id="menu-appbar"
                    anchorEl={anchorEl}
                    anchorOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                    }}
                    transformOrigin={{
                        vertical: 'top',
                        horizontal: 'right',
                    }}
                    open={userOpen}
                    onClose={this.props.handleCloseMenu}
                >
                    <MenuItem
                        onClick={this.props.handleCloseMenu}
                        button
                        component={Link}
                        to={'/' + i18n.language + URL_PROFIL}
                    >
                        {t('menu.profil')}
                    </MenuItem>
                    <MenuItem
                        onClick={this.props.handleCloseMenu}
                        button
                        component={Link}
                        to={'/' + i18n.language + URL_LOGOUT}
                    >
                        {t('menu.logout')}
                    </MenuItem>
                </Menu>
            </div>
        );
    }
}

export default UIAppBar;